class EmbeddedDataset():
    """
    Base class to compute the embeddings of a whole datasets.
    """
    
    def __init__(self, classes: List[str], templates: List[str], subsample_size: int = 10_000, split='', load_precomputed_embeddings=False):
        """
        Args:
            classes (list): list of class names
            templates (list): list of templates to build the prompts associated to the labels
        """
        self.subsample_size = subsample_size
        self.split = ''
        self.load_precomputed = False
        pass

    def embed(self, image_encoder: nn.Module, text_encoder: nn.Module) -> (torch.Tensor, torch.Tensor, torch.Tensor):
        """
        Embed the images and texts in this dataset.

        Args:
            image_encoder (nn.Module): an image encoder
            text_encoder (nn.Module): a text encoder

        Returns:
            zimgs (torch.Tensor): the image embeddings
            ztxts (torch.Tensor): the text embeddings
            im_labels (torch.Tensor): the image labels
        """
        if self.load_precomputed:
            if not os.path.exists(f'{self.__class__.__name__}{self.split}'):
                print('downloading precomputed embeddings...')
                key = drive_keys[f'{self.__class__.__name__}{self.split}']
                file_name = f'{self.__class__.__name__}{self.split}.tar'
                gdown.download('https://drive.google.com/uc?id=' + key, file_name, quiet=False)
                print(f'Unzipping {file_name}...')
                subprocess.call(f"tar -xf {file_name}", shell=True)
                # subprocess.call(['7z', 'x', f'{self.__class__.__name__}{self.split}.7z'])
            print('loading...')
            zimgs = torch.load(f'{self.__class__.__name__}{self.split}/zimgs_{image_encoder.__class__.__name__}.pt')
            ztxts = torch.load(f'{self.__class__.__name__}{self.split}/ztxts_{text_encoder.__class__.__name__}.pt')
            with open(f'{self.__class__.__name__}{self.split}/labels.pkl', 'rb') as f:
                im_labels = pickle.load(f)
        else:
            ztxts = text_encoder.encode(self.texts)
            zimgs, im_labels = embeddings_from_dl(image_encoder, self.loader, dl_with_labels=True)
        return zimgs[:self.subsample_size], ztxts, im_labels


