class EmbeddedPETS(EmbeddedDataset):
    """
    PETS loader.
    """
    def __init__(self, classes: List[str] = None, templates: List[str] = None, subsample_size: int = 10_000, sampler_seed: int = 42, load_precomputed_embeddings=False, split = 'test'):
        """
        Args:
            classes: a list of class names to use for zero-shot testing.
            templates: a list of templates to use for zero-shot testing.
            subsample_size: the number of images to use for zero-shot testing. (they will be sampled randomly)
            sampler_seed: the seed for the random sampler.
        """
        if not classes: self.classes = ['abyssinian cat', 'american bulldog dog', 'american pit bull terrier dog', 'basset hound dog', 'beagle dog', 'bengal cat', 'birman cat', 'bombay cat', 'boxer dog', 'british shorthair cat', 'chihuahua dog', 'egyptian mau cat', 'english cocker spaniel dog', 'english setter dog', 'german shorthaired dog', 'great pyrenees dog', 'havanese dog', 'japanese chin dog', 'keeshond dog', 'leonberger dog', 'maine coon cat', 'miniature pinscher dog', 'newfoundland dog', 'persian cat', 'pomeranian dog', 'pug dog', 'ragdoll cat', 'russian blue cat', 'saint bernard dog', 'samoyed dog', 'scottish terrier dog', 'shiba inu dog', 'siamese cat', 'sphynx cat', 'staffordshire bull terrier dog', 'wheaten terrier dog', 'yorkshire terrier dog']
        if not templates: self.templates = [
            'a type of pet {}.',
            'a {} texture.',
            '{} , an animal.',
        ]
        self.texts = [
            prompt.format(classname)
            for classname in self.classes
            for prompt in self.templates
        ]
        self.load_precomputed = load_precomputed_embeddings
        self.subsample_size = subsample_size
        self.split = ''
        if not self.load_precomputed:
            preprocess = transforms.Compose([transforms.ToTensor(),
                                            transforms.Normalize(mean=[0.485, 0.456, 0.406], std=[0.229, 0.224, 0.225]),
                                            transforms.Resize((224, 224))])
            images = datasets.OxfordIIITPet(root='pets', transform=preprocess, split={'Train' : 'trainval', 'Test' : 'test'}[split], download=True)
            self.loader = torch.utils.data.DataLoader(images, batch_size=100, num_workers=4)
